				function ff_plural($number, $text_arr) {
					$cases = array (2, 0, 1, 1, 1, 2);
					$text = $number . ' ' . $text_arr[ ($number % 100 > 4 && $number % 100 < 20) ? 2 : $cases[min($number % 10, 5)] ];
					return $text;
				}

